/**
 * Quote Calculator for Miami Alliance 3PL
 * Calculates storage, handling, pick & pack, and shipping estimates
 * Includes PDF quote generation with company branding
 */

const PRICING = {
  dimensionalFactor: 139, // DIM factor for domestic shipping
  storagePerCubicFtDay: 0.025, // $/cubic ft/day
  handlingFee: 3.5, // $ per unit
  pickAndPack: 1.25, // $ per item
  palletStoragePerDay: 0.75, // $/pallet/day
  blackWrapping: 7.0, // $ per pallet - added by Master Jorge
  shippingZones: {
    local: 0.45, // $/lb - Florida
    regional: 0.65, // $/lb - Southeast
    national: 0.85, // $/lb - National
    none: 0, // No shipping required
    dropship: 0, // Drop ship - charged by unit box size
  },
  dropShipRates: {
    envelope: 1.5, // $/unit - Envelopes
    small: 3.0, // $/unit - Small Box
    medium: 5.0, // $/unit - Medium Box
    large: 20.0, // $/unit - Large Box
  },
};

const COMPANY_INFO = {
  name: "MIAMI ALLIANCE 3PL",
  address: "8780 NW 100th ST",
  city: "Medley, FL 33178",
  phone: "(786) 873-8819",
  email: "info@miamialliance3pl.com",
  website: "www.miamialliance3pl.com",
};

// Company logo embedded as base64 (300px version for PDF)
const COMPANY_LOGO_BASE64 =
  "data:image/jpeg;base64,";

class QuoteCalculator {
  constructor() {
    this.packageType = "box";
    this.dimensions = { length: 12, width: 12, height: 12 };
    this.weight = 25;
    this.quantity = 1;
    this.shippingZone = "regional";
    this.storageDays = 30;
    this.blackWrapping = false; // Black wrapping option - $7/pallet
    this.dropShipQty = { envelope: 0, small: 0, medium: 0, large: 0 }; // Drop ship unit quantities

    this.init();
  }

  init() {
    // Package type toggle
    document.querySelectorAll(".package-option").forEach((btn) => {
      btn.addEventListener("click", (e) => {
        document
          .querySelectorAll(".package-option")
          .forEach((b) => b.classList.remove("active"));
        btn.classList.add("active");
        this.packageType = btn.dataset.type;

        // Set default pallet dimensions
        if (this.packageType === "pallet") {
          this.setDimensions(48, 40, 48);
          document.getElementById("dim-length").value = 48;
          document.getElementById("dim-width").value = 40;
          document.getElementById("dim-height").value = 48;
          document.getElementById("weight-slider").value = 500;
          document.getElementById("weight-input").value = 500;
          this.weight = 500;
        } else {
          this.setDimensions(12, 12, 12);
          document.getElementById("dim-length").value = 12;
          document.getElementById("dim-width").value = 12;
          document.getElementById("dim-height").value = 12;
          document.getElementById("weight-slider").value = 25;
          document.getElementById("weight-input").value = 25;
          this.weight = 25;
        }

        this.calculate();

        // Show/hide black wrapping option based on package type
        const blackWrapGroup = document.getElementById("black-wrapping-group");
        if (blackWrapGroup) {
          blackWrapGroup.style.display =
            this.packageType === "pallet" ? "block" : "none";
          // Reset checkbox when switching away from pallet
          if (this.packageType !== "pallet") {
            const checkbox = document.getElementById("black-wrapping");
            if (checkbox) checkbox.checked = false;
            this.blackWrapping = false;
          }
        }

        // Notify 3D viewer if available
        if (window.quote3D) {
          window.quote3D.setPackageType(this.packageType);
          window.quote3D.updateDimensions(
            this.dimensions.length,
            this.dimensions.width,
            this.dimensions.height,
          );
        }
      });
    });

    // Dimension inputs
    ["length", "width", "height"].forEach((dim) => {
      const input = document.getElementById(`dim-${dim}`);
      if (input) {
        input.addEventListener("input", (e) => {
          const value = Math.max(
            1,
            Math.min(120, parseInt(e.target.value) || 1),
          );
          this.dimensions[dim] = value;
          this.calculate();

          if (window.quote3D) {
            window.quote3D.updateDimensions(
              this.dimensions.length,
              this.dimensions.width,
              this.dimensions.height,
            );
          }
        });
      }
    });

    // Weight slider
    const weightSlider = document.getElementById("weight-slider");
    const weightInput = document.getElementById("weight-input");

    if (weightSlider) {
      weightSlider.addEventListener("input", (e) => {
        this.weight = parseInt(e.target.value);
        if (weightInput) weightInput.value = this.weight;
        this.calculate();
      });
    }

    if (weightInput) {
      weightInput.addEventListener("input", (e) => {
        this.weight = Math.max(
          1,
          Math.min(2000, parseInt(e.target.value) || 1),
        );
        if (weightSlider) weightSlider.value = Math.min(500, this.weight);
        this.calculate();
      });
    }

    // Quantity controls
    const qtyInput = document.getElementById("quantity-input");
    const qtyBtns = document.querySelectorAll(".qty-btn");

    qtyBtns.forEach((btn) => {
      btn.addEventListener("click", () => {
        if (btn.dataset.action === "increase") {
          this.quantity = Math.min(1000, this.quantity + 1);
        } else {
          this.quantity = Math.max(1, this.quantity - 1);
        }
        if (qtyInput) qtyInput.value = this.quantity;
        this.calculate();
      });
    });

    if (qtyInput) {
      qtyInput.addEventListener("input", (e) => {
        this.quantity = Math.max(
          1,
          Math.min(1000, parseInt(e.target.value) || 1),
        );
        this.calculate();
      });
    }

    // Shipping zone
    const zoneSelect = document.getElementById("shipping-zone");
    if (zoneSelect) {
      zoneSelect.addEventListener("change", (e) => {
        this.shippingZone = e.target.value;
        // Show/hide drop ship section
        const dropshipGroup = document.getElementById("dropship-group");
        if (dropshipGroup) {
          dropshipGroup.style.display =
            this.shippingZone === "dropship" ? "block" : "none";
          // Reset drop ship quantities when switching away
          if (this.shippingZone !== "dropship") {
            this.dropShipQty = { envelope: 0, small: 0, medium: 0, large: 0 };
            ["envelope", "small", "medium", "large"].forEach((size) => {
              const input = document.getElementById(`dropship-${size}`);
              if (input) input.value = 0;
            });
          }
        }
        this.calculate();
      });
    }

    // Drop ship unit quantity controls
    document.querySelectorAll(".dropship-btn").forEach((btn) => {
      btn.addEventListener("click", () => {
        const box = btn.dataset.box;
        const input = document.getElementById(`dropship-${box}`);
        if (btn.dataset.action === "increase") {
          this.dropShipQty[box] = Math.min(9999, this.dropShipQty[box] + 1);
        } else {
          this.dropShipQty[box] = Math.max(0, this.dropShipQty[box] - 1);
        }
        if (input) input.value = this.dropShipQty[box];
        this.calculate();
      });
    });

    // Drop ship unit quantity inputs
    ["envelope", "small", "medium", "large"].forEach((size) => {
      const input = document.getElementById(`dropship-${size}`);
      if (input) {
        input.addEventListener("input", (e) => {
          this.dropShipQty[size] = Math.max(
            0,
            Math.min(9999, parseInt(e.target.value) || 0),
          );
          this.calculate();
        });
      }
    });

    // Black wrapping checkbox (pallets only - $7/pallet)
    const blackWrappingCheck = document.getElementById("black-wrapping");
    if (blackWrappingCheck) {
      blackWrappingCheck.addEventListener("change", (e) => {
        this.blackWrapping = e.target.checked;
        this.calculate();
      });
    }

    // Initial calculation
    this.calculate();
  }

  setDimensions(length, width, height) {
    this.dimensions = { length, width, height };
  }

  getCubicFeet() {
    const { length, width, height } = this.dimensions;
    return (length * width * height) / 1728; // cubic inches to cubic feet
  }

  getDimensionalWeight() {
    const { length, width, height } = this.dimensions;
    return (length * width * height) / PRICING.dimensionalFactor;
  }

  getBillableWeight() {
    return Math.max(this.weight, this.getDimensionalWeight());
  }

  calculate() {
    const cubicFt = this.getCubicFeet();
    const dimWeight = this.getDimensionalWeight();
    const billableWeight = this.getBillableWeight();

    let storage,
      handling,
      pickPack,
      shipping,
      wrapping = 0,
      dropship = 0;

    if (this.packageType === "pallet") {
      // Pallet pricing
      storage = PRICING.palletStoragePerDay * this.storageDays * this.quantity;
      handling = 15.0 * this.quantity; // Higher handling for pallets
      pickPack = 5.0 * this.quantity; // Higher pick/pack for pallets
      // Shipping based on zone type
      if (this.shippingZone === "none") {
        shipping = 0;
      } else if (this.shippingZone === "dropship") {
        shipping = 0;
        dropship = this.calculateDropShipTotal();
      } else {
        shipping =
          billableWeight *
          PRICING.shippingZones[this.shippingZone] *
          this.quantity;
      }
      // Black wrapping option - $7/pallet (added by Master Jorge)
      if (this.blackWrapping) {
        wrapping = PRICING.blackWrapping * this.quantity;
      }
    } else {
      // Box pricing
      storage =
        cubicFt *
        PRICING.storagePerCubicFtDay *
        this.storageDays *
        this.quantity;
      handling = PRICING.handlingFee * this.quantity;
      pickPack = PRICING.pickAndPack * this.quantity;
      // Shipping based on zone type
      if (this.shippingZone === "none") {
        shipping = 0;
      } else if (this.shippingZone === "dropship") {
        shipping = 0;
        dropship = this.calculateDropShipTotal();
      } else {
        shipping =
          billableWeight *
          PRICING.shippingZones[this.shippingZone] *
          this.quantity;
      }
    }

    // Minimum storage charge
    storage = Math.max(storage, 5.0);

    const total =
      storage + handling + pickPack + shipping + wrapping + dropship;

    // Update UI
    this.updateDisplay({
      dimWeight: dimWeight.toFixed(1),
      cubicFt: cubicFt.toFixed(1),
      storage: storage,
      handling: handling,
      pickPack: pickPack,
      shipping: shipping,
      wrapping: wrapping,
      dropship: dropship,
      total: total,
    });

    // Track quote calculation (debounced)
    if (window.MA3PLAnalytics && !this._calcDebounce) {
      this._calcDebounce = setTimeout(() => {
        MA3PLAnalytics.trackQuoteCalculation(
          this.packageType,
          `${this.dimensions.length}x${this.dimensions.width}x${this.dimensions.height}`,
          this.quantity,
          total,
        );
        this._calcDebounce = null;
      }, 2000);
    }

    return { storage, handling, pickPack, shipping, wrapping, dropship, total };
  }

  updateDisplay(values) {
    const elements = {
      "result-dim-weight": `${values.dimWeight} lbs`,
      "result-cubic-ft": `${values.cubicFt} ft`,
      "result-storage": this.formatCurrency(values.storage),
      "result-handling": this.formatCurrency(values.handling),
      "result-pickpack": this.formatCurrency(values.pickPack),
      "result-shipping": this.formatCurrency(values.shipping),
      "result-wrapping": this.formatCurrency(values.wrapping),
      "result-dropship": this.formatCurrency(values.dropship),
      "result-total": this.formatCurrency(values.total),
    };

    for (const [id, value] of Object.entries(elements)) {
      const el = document.getElementById(id);
      if (el) el.textContent = value;
    }

    // Show/hide wrapping row based on pallet + option selected
    const wrappingRow = document.getElementById("wrapping-row");
    if (wrappingRow) {
      wrappingRow.style.display =
        this.packageType === "pallet" && this.blackWrapping ? "flex" : "none";
    }

    // Show/hide drop ship row
    const dropshipRow = document.getElementById("dropship-row");
    if (dropshipRow) {
      dropshipRow.style.display =
        this.shippingZone === "dropship" && values.dropship > 0
          ? "flex"
          : "none";
    }

    // Show/hide shipping row (hide when no shipping or dropship)
    const shippingEl = document.getElementById("result-shipping");
    if (shippingEl && shippingEl.parentElement) {
      shippingEl.parentElement.style.display =
        this.shippingZone === "none" || this.shippingZone === "dropship"
          ? "none"
          : "flex";
    }
  }

  calculateDropShipTotal() {
    let total = 0;
    for (const [size, qty] of Object.entries(this.dropShipQty)) {
      total += qty * PRICING.dropShipRates[size];
    }
    return total;
  }

  getDropShipBreakdown() {
    const lines = [];
    const labels = {
      envelope: "Envelopes",
      small: "Small Box",
      medium: "Medium Box",
      large: "Large Box",
    };
    for (const [size, qty] of Object.entries(this.dropShipQty)) {
      if (qty > 0) {
        lines.push({
          label: labels[size],
          qty: qty,
          rate: PRICING.dropShipRates[size],
          total: qty * PRICING.dropShipRates[size],
        });
      }
    }
    return lines;
  }

  formatCurrency(amount) {
    return "$" + amount.toFixed(2).replace(/\B(?=(\d{3})+(?!\d))/g, ",");
  }

  generateQuoteNumber() {
    const date = new Date();
    const dateStr = date.toISOString().slice(0, 10).replace(/-/g, "");
    const random = Math.floor(Math.random() * 10000)
      .toString()
      .padStart(4, "0");
    return `MA3PL-${dateStr}-${random}`;
  }

  getZoneName(zone) {
    const names = {
      local: "Local (Florida)",
      regional: "Regional (Southeast)",
      national: "National",
      none: "No Shipping Required",
      dropship: "Drop Ship (by unit)",
    };
    return names[zone] || zone;
  }

  generatePDF() {
    // Check if jsPDF is loaded
    if (typeof window.jspdf === "undefined") {
      alert("PDF library loading... Please try again in a moment.");
      return;
    }

    const { jsPDF } = window.jspdf;
    const doc = new jsPDF();

    const quoteNumber = this.generateQuoteNumber();
    const currentDate = new Date().toLocaleDateString("en-US", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });

    // Colors
    const primaryColor = [30, 58, 95]; // #1e3a5f
    const accentColor = [20, 184, 166]; // #14b8a6
    const grayColor = [100, 116, 139]; // #64748b
    const lightGray = [241, 245, 249]; // #f1f5f9
    const darkNavy = [15, 23, 42]; // #0f172a

    // Calculate values
    const cubicFt = this.getCubicFeet();
    const dimWeight = this.getDimensionalWeight();
    const billableWeight = this.getBillableWeight();
    const results = this.calculate();

    // ===== HEADER - DRAMATIC GRADIENT BAR =====
    // Dark gradient background
    doc.setFillColor(...darkNavy);
    doc.rect(0, 0, 210, 55, "F");

    // Accent stripe
    doc.setFillColor(...accentColor);
    doc.rect(0, 55, 210, 3, "F");

    // Add company logo (embedded base64) - maintains portrait aspect ratio (784x1168 = ~2:3)
    // Logo dimensions: 28mm wide x 42mm tall to match original proportions
    try {
      doc.addImage(COMPANY_LOGO_BASE64, "JPEG", 10, 6, 28, 42);
    } catch (e) {
      console.log("Logo could not be added:", e);
    }

    // Company name - large and bold (positioned after logo)
    doc.setFont("helvetica", "bold");
    doc.setFontSize(28);
    doc.setTextColor(255, 255, 255);
    doc.text("MIAMI ALLIANCE", 44, 25);

    // 3PL in accent color
    doc.setTextColor(...accentColor);
    doc.text("3PL", 44, 40);

    // Tagline
    doc.setFont("helvetica", "normal");
    doc.setFontSize(11);
    doc.setTextColor(148, 163, 184);
    doc.text("WAREHOUSING  |  FULFILLMENT  |  LOGISTICS", 44, 50);

    // Quote badge on right
    doc.setFillColor(...accentColor);
    doc.roundedRect(145, 10, 55, 35, 3, 3, "F");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(10);
    doc.setTextColor(255, 255, 255);
    doc.text("INSTANT", 172, 22, { align: "center" });
    doc.setFontSize(16);
    doc.text("QUOTE", 172, 35, { align: "center" });

    // ===== QUOTE INFO BAR =====
    doc.setFillColor(...lightGray);
    doc.rect(0, 58, 210, 20, "F");

    doc.setFont("helvetica", "bold");
    doc.setFontSize(10);
    doc.setTextColor(...primaryColor);
    doc.text(`QUOTE #: ${quoteNumber}`, 20, 70);

    doc.setFont("helvetica", "normal");
    doc.setTextColor(...grayColor);
    doc.text(`Date: ${currentDate}`, 105, 70, { align: "center" });
    doc.text("Valid for 30 days", 190, 70, { align: "right" });

    // ===== PACKAGE DETAILS - LEFT COLUMN =====
    let yPos = 90;
    const leftCol = 20;
    const rightCol = 110;

    // Section header with icon
    doc.setFillColor(...primaryColor);
    doc.roundedRect(leftCol, yPos - 6, 80, 10, 2, 2, "F");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(11);
    doc.setTextColor(255, 255, 255);
    doc.text("PACKAGE DETAILS", leftCol + 5, yPos + 1);

    yPos += 12;

    // Package info with styled rows
    const packageDetails = [
      ["Type", this.packageType === "pallet" ? "PALLET" : "BOX"],
      [
        "Dimensions",
        `${this.dimensions.length}" x ${this.dimensions.width}" x ${this.dimensions.height}"`,
      ],
      ["Weight", `${this.weight} lbs (actual)`],
      ["DIM Weight", `${dimWeight.toFixed(1)} lbs`],
      ["Billable", `${billableWeight.toFixed(1)} lbs`],
      ["Volume", `${cubicFt.toFixed(2)} cu ft`],
      ["Quantity", `${this.quantity} unit${this.quantity > 1 ? "s" : ""}`],
      ["Zone", this.getZoneName(this.shippingZone)],
    ];

    doc.setFontSize(9);
    packageDetails.forEach(([label, value], index) => {
      const rowY = yPos + index * 7;
      if (index % 2 === 0) {
        doc.setFillColor(248, 250, 252);
        doc.rect(leftCol, rowY - 4, 80, 7, "F");
      }
      doc.setFont("helvetica", "normal");
      doc.setTextColor(...grayColor);
      doc.text(label + ":", leftCol + 3, rowY);
      doc.setFont("helvetica", "bold");
      doc.setTextColor(...primaryColor);
      doc.text(value, leftCol + 77, rowY, { align: "right" });
    });

    // ===== PRICING BREAKDOWN - RIGHT COLUMN =====
    yPos = 90;

    // Section header
    doc.setFillColor(...accentColor);
    doc.roundedRect(rightCol, yPos - 6, 80, 10, 2, 2, "F");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(11);
    doc.setTextColor(255, 255, 255);
    doc.text("PRICING BREAKDOWN", rightCol + 5, yPos + 1);

    yPos += 12;

    // Pricing rows
    const priceRows = [
      [
        "Storage (" + this.storageDays + "d)",
        this.formatCurrency(results.storage),
      ],
      ["Handling Fee", this.formatCurrency(results.handling)],
      ["Pick & Pack", this.formatCurrency(results.pickPack)],
    ];
    // Add shipping or dropship or nothing
    if (this.shippingZone === "none") {
      priceRows.push(["Shipping", "N/A"]);
    } else if (this.shippingZone === "dropship") {
      // Add each dropship line item
      const breakdown = this.getDropShipBreakdown();
      if (breakdown.length > 0) {
        breakdown.forEach((item) => {
          priceRows.push([
            `${item.label} x${item.qty}`,
            this.formatCurrency(item.total),
          ]);
        });
      } else {
        priceRows.push(["Drop Ship", "$0.00"]);
      }
    } else {
      priceRows.push(["Est. Shipping", this.formatCurrency(results.shipping)]);
    }
    // Add black wrapping if selected (pallet only)
    if (this.packageType === "pallet" && this.blackWrapping) {
      priceRows.push(["Black Wrapping", this.formatCurrency(results.wrapping)]);
    }

    doc.setFontSize(10);
    priceRows.forEach(([service, amount], index) => {
      const rowY = yPos + index * 9;
      if (index % 2 === 0) {
        doc.setFillColor(248, 250, 252);
        doc.rect(rightCol, rowY - 4, 80, 9, "F");
      }
      doc.setFont("helvetica", "normal");
      doc.setTextColor(...grayColor);
      doc.text(service, rightCol + 3, rowY + 1);
      doc.setFont("helvetica", "bold");
      doc.setTextColor(...primaryColor);
      doc.text(amount, rightCol + 77, rowY + 1, { align: "right" });
    });

    // TOTAL - Big and bold (adjust for extra rows)
    const extraRows = priceRows.length - 4; // base is 4 rows (storage, handling, pick&pack, shipping)
    const totalY = yPos + 45 + (extraRows > 0 ? extraRows * 9 : 0);
    doc.setFillColor(...primaryColor);
    doc.roundedRect(rightCol, totalY - 6, 80, 16, 2, 2, "F");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(11);
    doc.setTextColor(255, 255, 255);
    doc.text("TOTAL", rightCol + 5, totalY + 3);
    doc.setFontSize(16);
    doc.setTextColor(...accentColor);
    doc.text(this.formatCurrency(results.total), rightCol + 75, totalY + 4, {
      align: "right",
    });

    // ===== RATE DETAILS =====
    yPos = 165;

    doc.setDrawColor(...lightGray);
    doc.setLineWidth(0.5);
    doc.line(20, yPos, 190, yPos);

    yPos += 10;
    doc.setFont("helvetica", "bold");
    doc.setFontSize(10);
    doc.setTextColor(...primaryColor);
    doc.text("RATE DETAILS", 20, yPos);

    yPos += 8;
    doc.setFont("helvetica", "normal");
    doc.setFontSize(8);
    doc.setTextColor(...grayColor);

    const rates = [
      `Storage: ${this.packageType === "pallet" ? "$0.75/pallet/day" : "$0.025/cu ft/day"}`,
      `Handling: ${this.packageType === "pallet" ? "$15.00/pallet" : "$3.50/unit"}`,
      `Pick & Pack: ${this.packageType === "pallet" ? "$5.00/pallet" : "$1.25/item"}`,
    ];
    // Add shipping rate detail based on zone
    if (this.shippingZone === "none") {
      rates.push("Shipping: N/A");
    } else if (this.shippingZone === "dropship") {
      rates.push("Drop Ship: Env $1.50 | Sm $3.00 | Med $5.00 | Lg $20.00");
    } else {
      rates.push(
        `Shipping: $${PRICING.shippingZones[this.shippingZone].toFixed(2)}/lb (${this.getZoneName(this.shippingZone)})`,
      );
    }
    // Add black wrapping rate if applicable
    if (this.packageType === "pallet") {
      rates.push(`Black Wrap: $${PRICING.blackWrapping.toFixed(2)}/pallet`);
    }
    doc.text(rates.join("   |   "), 105, yPos, { align: "center" });

    // ===== IMPORTANT NOTES =====
    yPos += 15;

    doc.setFillColor(254, 249, 195); // Yellow background
    doc.roundedRect(20, yPos - 5, 170, 35, 3, 3, "F");
    doc.setDrawColor(250, 204, 21);
    doc.setLineWidth(0.5);
    doc.roundedRect(20, yPos - 5, 170, 35, 3, 3, "S");

    doc.setFont("helvetica", "bold");
    doc.setFontSize(9);
    doc.setTextColor(146, 64, 14);
    doc.text("IMPORTANT NOTES", 25, yPos + 3);

    doc.setFont("helvetica", "normal");
    doc.setFontSize(7);
    const notes = [
      "This is an estimate. Final pricing may vary based on actual dimensions and services.",
      "Volume discounts available for recurring shipments. Quote valid for 30 days.",
      "Special handling, hazmat, or oversized items may incur additional fees.",
    ];
    notes.forEach((note, i) => {
      doc.text(" " + note, 25, yPos + 11 + i * 6);
    });

    // ===== CALL TO ACTION =====
    yPos = 235;

    doc.setFillColor(...primaryColor);
    doc.roundedRect(20, yPos, 170, 25, 3, 3, "F");

    doc.setFont("helvetica", "bold");
    doc.setFontSize(14);
    doc.setTextColor(255, 255, 255);
    doc.text("Ready to get started?", 30, yPos + 11);

    doc.setFont("helvetica", "normal");
    doc.setFontSize(10);
    doc.text(
      "Contact us for a detailed quote tailored to your needs.",
      30,
      yPos + 20,
    );

    // Contact button style
    doc.setFillColor(...accentColor);
    doc.roundedRect(145, yPos + 5, 40, 15, 2, 2, "F");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(9);
    doc.text("CONTACT", 165, yPos + 15, { align: "center" });

    // ===== FOOTER =====
    const footerY = 275;

    doc.setDrawColor(...lightGray);
    doc.line(20, footerY, 190, footerY);

    doc.setFont("helvetica", "normal");
    doc.setFontSize(8);
    doc.setTextColor(...grayColor);
    doc.text(
      COMPANY_INFO.address + ", " + COMPANY_INFO.city,
      105,
      footerY + 7,
      { align: "center" },
    );
    doc.text(
      `${COMPANY_INFO.phone}  |  ${COMPANY_INFO.email}  |  ${COMPANY_INFO.website}`,
      105,
      footerY + 13,
      { align: "center" },
    );

    // Track PDF download
    if (window.MA3PLAnalytics) {
      MA3PLAnalytics.trackPDFDownload(quoteNumber, results.total);
    }

    // Save PDF
    doc.save(`MiamiAlliance3PL_Quote_${quoteNumber}.pdf`);

    // Show success message
    this.showPDFSuccess(quoteNumber);
  }

  showPDFSuccess(quoteNumber) {
    // Create success toast
    const toast = document.createElement("div");
    toast.className = "pdf-toast";
    toast.innerHTML = `
            <div class="pdf-toast-icon"></div>
            <div class="pdf-toast-content">
                <strong>Quote Downloaded!</strong>
                <span>${quoteNumber}</span>
            </div>
        `;
    document.body.appendChild(toast);

    // Animate in
    setTimeout(() => toast.classList.add("show"), 100);

    // Remove after 4 seconds
    setTimeout(() => {
      toast.classList.remove("show");
      setTimeout(() => toast.remove(), 300);
    }, 4000);
  }
}

// Initialize when DOM is ready
document.addEventListener("DOMContentLoaded", () => {
  window.quoteCalculator = new QuoteCalculator();

  // Add PDF button event listener
  const pdfBtn = document.getElementById("download-pdf-btn");
  if (pdfBtn) {
    pdfBtn.addEventListener("click", () => {
      window.quoteCalculator.generatePDF();
    });
  }
});
